    @IBAction func backButt(_ sender: Any) {
        if email == "" {
        self.performSegue(withIdentifier: "termsToRegister", sender: self)
        }
        else if email != "" {
            self.performSegue(withIdentifier: "termsToSecond", sender: self)
        }
    }
    
   
    @IBAction func viewPrivacyButt(_ sender: Any) {
        self.performSegue(withIdentifier: "termsToPrivacy", sender: self)
    }
    
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        
        if segue.identifier == "termsToRegister" {
            let DestViewController : RegisterScreen = segue.destination as! RegisterScreen
            DestViewController.email = email
            DestViewController.userName = userName
        }
            
        else if segue.identifier == "termsToSecond" {
            let DestViewController : SecondScreen = segue.destination as! SecondScreen
            DestViewController.email = email
            DestViewController.tokenNum = tokenNum
            DestViewController.userName = userName
        }
        
        else if segue.identifier == "termsToPrivacy" {
            let DestViewController : PrivacyScreen = segue.destination as! PrivacyScreen
            DestViewController.email = email
            DestViewController.tokenNum = tokenNum
            DestViewController.userName = userName
        }
    }


